import React from "react";
import CRUD from "../services/crud";
import { Form, FormGroup, Button, Input, Label } from "reactstrap";

function FormInput(props) {
  const [postData, setPostData] = React.useState({

    customer_name: "",
    contact_name: "",
    address: "",
    city: "Da Nang",
    postal_code: "45000",
    country: "VN",
  });

  function handleChangeCustomerName(e) {
    e.preventDefault();
    setPostData({ ...postData, customer_name: e.target.value }); 
  }
  function handleChangeContactName(e) {
    e.preventDefault();
    setPostData({ ...postData, contact_name: e.target.value }); 
  }
  function handleChangeAddress(e) {
    e.preventDefault();
    setPostData({ ...postData, address: e.target.value });
  }

  function handleOnClickSubmit(e) {

    e.preventDefault();

  }

  return (
    <Form onSubmit={handleOnSubmit}>
      <FormGroup>
        <Label for="customerName">Customer Name</Label>
        <Input
          type="text"
          name="customer_name"
          value={postData.customer_name}
          onChange={handleChangeData}
          placeholder="Customer Name"
        />
      </FormGroup>
      <FormGroup>
        <Label for="contactName">Contact Name</Label>
        <Input
          type="text"
          name="contact_name"
          value={postData.contact_name}
          onChange={handleChangeData}
          placeholder="Contact Name"
        />
      </FormGroup>
      <FormGroup>
        <Label for="_address">Address</Label>
        <Input
          type="text"
          name="address"
          value={postData.address}
          onChange={handleChangeData}
          placeholder="Address"
          id=""
        />
      </FormGroup>

      <input type="text" name="city" value="this is city" hidden />
      <input type="text" name="postalCode" value="this is postalCode" hidden />
      <input type="text" name="country" value="this is country" hidden />
      <div className="text-center">
        <Button
          name="btnSubmit"
          value="Submit"
          onClick={handleOnClickSubmit}
          color="primary"
        >
          Submit{" "}
        </Button>
      </div>
    </Form>
  );
}
export default FormInput;
